import { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");
  const [theme, setTheme] = useState("dark"); // "dark" | "light"

  const askAI = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSongs([]);
    setErrorMsg("");

    try {
      const res = await axios.post("http://localhost:3000/api/recommend", {
        message: query,
      });

      const results = res.data.songs || [];

      if (results.length === 0) {
        setErrorMsg("No Spotify previews available for this mood üò¢");
      }

      setSongs(results);
    } catch (err) {
      console.error("FRONTEND ERROR:", err);
      setErrorMsg("Server error. Try again later.");
    }

    setLoading(false);
  };

  const toggleTheme = () => {
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  };

  const skeletonArray = Array.from({ length: 6 });

  return (
    <div className={`app-container ${theme}`}>

      {/* Top bar: title + theme toggle + music bars */}
      <div className="hero">
        <h1 className="main-title">üéß AI Smart Music Player</h1>
        <p className="subtitle">Tell your mood / songs ‚Üí AI picks the perfect Spotify tracks üéµ</p>

        <div className="hero-controls">
          <div className="music-bars">
            <span className="bar bar1" />
            <span className="bar bar2" />
            <span className="bar bar3" />
            <span className="bar bar4" />
          </div>

          <button className="theme-toggle" onClick={toggleTheme}>
            {theme === "dark" ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
          </button>
        </div>
      </div>


      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          className="search-input"
          placeholder="Try: sad hindi songs, arijit singh, 90s bollywood..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && !loading && askAI()}
        />

        <button
          className="search-btn"
          onClick={askAI}
          disabled={loading}
        >
          {loading ? "Searching..." : "Find Music"}
        </button>
      </div>

      {/* Loading Message */}
      {loading && <p className="loader">‚è≥ Finding the perfect vibe...</p>}

      {/* Error Message */}
      {errorMsg && <p className="error-msg">{errorMsg}</p>}

      {/* Songs / Skeleton */}
      <div className="song-grid">
        {loading
          ? skeletonArray.map((_, index) => (
            <div className="song-card skeleton" key={index}>
              <div className="thumbnail skeleton-thumb" />
              <div className="song-info">
                <div className="skeleton-line line-lg" />
                <div className="skeleton-line line-sm" />
                <div className="skeleton-line line-sm" />
              </div>
            </div>
          ))
          : songs.map((song, index) => (
            <div
              className="song-card"
              key={index}
              onClick={() => window.open(song.spotify_url, "_blank")}
              style={{ cursor: "pointer", animationDelay: `${index * 0.07}s` }}
            >
              <div className="thumbnail-wrapper">
                <img
                  src={song.image}
                  alt={song.title}
                  className="thumbnail"
                />
                <div className="play-overlay">
                  ‚ñ∂
                </div>
              </div>

              <div className="song-info">
                <h2 className="song-title">{song.title}</h2>
                <p className="song-artist">{song.artist}</p>

                {song.preview_url ? (
                  <audio controls className="audio-player">
                    <source src={song.preview_url} />
                  </audio>
                ) : (
                  <p className="no-preview">‚ùå No Preview Available</p>
                )}
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default App;
