import { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);

  const askAI = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSongs([]);

    try {
      const res = await axios.post("http://localhost:3000/api/recommend", {
        message: query,
      });

      setSongs(res.data.songs);
    } catch (err) {
      console.error("FRONTEND ERROR:", err);
    }

    setLoading(false);
  };

  return (
    <div className="container">
      <h1 className="title">üéµ AI Music Player</h1>

      <div className="search-box">
        <input
          type="text"
          className="search-input"
          placeholder="Ask AI: play sad songs, romantic songs..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button className="search-btn" onClick={askAI}>
          Search
        </button>
      </div>

      {loading && <p className="loading">‚è≥ Finding songs...</p>}

      <div className="songs">
        {songs.map((song, index) => (
          <div className="song-card" key={index}>
            <img src={song.image} alt="album" className="song-image" />

            <div className="song-details">
              <h2 className="song-title">{song.title}</h2>
              <p className="song-artist">{song.artist}</p>

              {song.preview_url ? (
                <audio controls className="audio-player">
                  <source src={song.preview_url} />
                </audio>
              ) : (
                <p className="no-preview">No preview available ‚ùå</p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
