import { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);

  const askAI = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSongs([]);

    try {
      const res = await axios.post("http://localhost:3000/api/recommend", {
        message: query,
      });

      setSongs(res.data.songs);
    } catch (err) {
      console.error("FRONTEND ERROR:", err);
    }

    setLoading(false);
  };

  return (
    <div className="app-container">

      <h1 className="main-title">üéß AI Smart Music Player</h1>
      <p className="subtitle">
        Tell your mood ‚Üí AI picks the perfect songs üéµ
      </p>

      {/* Search */}
      <div className="search-bar">
        <input
          type="text"
          className="search-input"
          placeholder="Try: sad songs, happy beats, romantic vibes..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && askAI()}
        />
        <button className="search-btn" onClick={askAI}>Find Music</button>
      </div>

      {loading && <p className="loader">‚è≥ AI is finding your music...</p>}

      {/* Song List */}
      <div className="song-grid">
        {songs.map((song, index) => (
          <div className="song-card" key={index}>

            <img src={song.image} alt="" className="thumbnail" />

            <div className="song-info">
              <h2 className="song-title">{song.title}</h2>
              <p className="song-artist">{song.artist}</p>

              {song.preview_url ? (
                <audio controls className="audio-player">
                  <source src={song.preview_url} />
                </audio>
              ) : (
                <p className="no-preview">No Preview Available ‚ùå</p>
              )}
            </div>

          </div>
        ))}
      </div>

    </div>
  );
}

export default App;
