import { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  const askAI = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSongs([]);
    setErrorMsg("");

    try {
      const res = await axios.post("http://localhost:3000/api/recommend", {
        message: query,
      });

      const results = res.data.songs || [];

      if (results.length === 0) {
        setErrorMsg("No Spotify previews available for this mood üò¢");
      }

      setSongs(results);
    } catch (err) {
      console.error("FRONTEND ERROR:", err);
      setErrorMsg("Server error. Try again later.");
    }

    setLoading(false);
  };

  return (
    <div className="app-container">

      <h1 className="main-title">üéß AI Smart Music Player</h1>
      <p className="subtitle">Tell your mood/Songs ‚Üí AI picks the perfect Spotify songs üéµ</p>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          className="search-input"
          placeholder="Try: sad songs, happy beats, romantic hits..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && !loading && askAI()}
        />

        <button 
          className="search-btn" 
          onClick={askAI}
          disabled={loading}
        >
          {loading ? "Searching..." : "Find Music"}
        </button>
      </div>

      {/* Loading Message */}
      {loading && <p className="loader">‚è≥ Fetching Spotify previews...</p>}

      {/* Error Message */}
      {errorMsg && <p className="error-msg">{errorMsg}</p>}

      {/* Songs */}
      <div className="song-grid" >
        {songs.map((song, index) => (
          <div className="song-card" 
            key={index}
            onClick={() => window.open(song.spotify_url, "_blank")}
            style={{cursor:"Pointer"}}>

            <img src={song.image} alt={song.title} className="thumbnail" />

            <div className="song-info">
              <h2 className="song-title">{song.title}</h2>
              <p className="song-artist">{song.artist}</p>

              {song.preview_url ? (
                <audio controls className="audio-player">
                  <source src={song.preview_url} />
                </audio>
              ) : (
                <p className="no-preview">‚ùå No Preview Available</p>
              )}
            </div>

          </div>
        ))}
      </div>

    </div>
  );
}

export default App;
